import React, { useState, useContext } from 'react';
import './Pe칞as.css';
import carrinho from '../Assets/Assets/adccarrinho.png'
import  { CartContext }  from '../../Context/CartContext';


const Pe칞as = (props) => {

  const [showModal, setShowModal] = useState(false);
  const { addItemToCart } = useContext(CartContext);

  const handleCartClick = () => {
    setShowModal(true); // Abre o modal
  };

  const closeModal = () => {
    setShowModal(false); // Fecha o modal
  };

  const addToCart = () => {
    // Implementar l칩gica de adicionar ao carrinho
    // alert(`Produto ${props.name} adicionado ao carrinho!`);
    addItemToCart(props); // Adiciona a pe칞a ao carrinho
    setShowModal(false); // Fecha o modal ap칩s adicionar
  };

  return (
    <div className="pe칞as">
      <div className="cart-icon" onClick={handleCartClick}>
        <img src={carrinho} alt={''} />
      </div>
      <img src={props.image} alt={props.name} />
      <p>{props.name}</p>
      <div className="pe칞as-pre칞os">
        <div className="pe칞as-pre칞os-novos">
          R$ {props.new_price.toFixed(2)}
        </div>
        {props.old_price && (
          <div className="pe칞as-pre칞os-velhos">
            <del>R$ {props.old_price.toFixed(2)}</del>
          </div>
        )}
      </div>

      {/* Modal */}
      {showModal && (
  <div className="modal-overlay">
    <div className="modal-horizontal">
      {/* Bot칚o de fechar */}
      <button className="close-modal" onClick={closeModal}>
        &times; {/* 칈cone de fechar */}
      </button>

      {/* Conte칰do do modal */}
      <div className="modal-image-container">
        <img src={props.image} alt={props.name} className="modal-image" />
      </div>
      <div className="modal-details">
        <h2>{props.name}</h2>
        <div className="size-options">
          <p>Tamanho:</p>
          {[34, 35, 36, 37, 38, 39, 40].map((size) => (
            <button key={size} className="size-option">
              {size}
            </button>
          ))}
        </div>
        <button className="add-to-cart" onClick={addToCart}>
          Adicionar ao carrinho 游뇦n        </button>
      </div>
    </div>
  </div>
)}

    </div>
  );
};

export default Pe칞as;
