import React, { useState, useContext } from 'react';
import './Navbar.css';
import logobranco from '../Assets/logo-branco.png';
import cart_icon from '../Assets/icone-carrinho.png';
import search_icon from '../Assets/icone-search.png';
import profile_icon from '../Assets/icone-conta.png';
import menu_icon from '../Assets/menu-hamburguer.png';  // Adicione um ícone de menu hamburguer
import close_icon from '../Assets/close-icon.png';  // Adicione um ícone de fechar
import { Link } from 'react-router-dom';
import { CartContext } from '../../../Context/CartContext';

const Navbar = () => {
  const { cartItems } = useContext(CartContext);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <>
      <div className="navbar">
        <div className="nav-left">
          <img src={logobranco} alt="Logo Bebecê" className="nav-logo" />
          <img
            src={menu_icon}
            alt="Menu"
            className="mobile-menu-icon"
            onClick={toggleMobileMenu}
          />
        </div>
        <div className="nav-right">
          <img src={search_icon} alt="Pesquisa" className="nav-icon" />
          <Link to="perfil">
            <img src={profile_icon} alt="Perfil" className="nav-icon" />
          </Link>
          <Link to="/carrinho">
            <div className="nav-cart">
              <img src={cart_icon} alt="Carrinho" className="nav-icon" />
              <span className={`cart-count ${cartItems.length > 0 ? 'show' : ''}`}>
                {cartItems.length}
              </span>
            </div>
          </Link>
        </div>
      </div>

      {isMobileMenuOpen && (
        <div className="mobile-menu">
          <img
            src={close_icon}
            alt="Fechar"
            className="close-icon"
            onClick={toggleMobileMenu}
          />
          <ul className="mobile-menu-list">
            <li>
              <Link to="lancamentos" onClick={toggleMobileMenu}>Lançamentos</Link>
            </li>
            <li>
              <Link to="outlet" onClick={toggleMobileMenu}>Outlet</Link>
            </li>
            <li>
              <Link to="botas" onClick={toggleMobileMenu}>Botas</Link>
            </li>
            <li>
              <Link to="tenis" onClick={toggleMobileMenu}>Tênis</Link>
            </li>
          </ul>
        </div>
      )}
    </>
  );
};

export default Navbar;
